# :command.parse_requirements
parse_requirements() {
  local key

  # :command.fixed_flags_filter
  while [[ $# -gt 0 ]]; do
    key="$1"
    case "$key" in
      --version | -v)
        version_command
        exit
        ;;

      --help | -h)
        long_usage=yes
        benchmark.sh_usage
        exit
        ;;

      *)
        break
        ;;

    esac
  done

  # :command.command_filter
  action=${1:-}

  case $action in
    -*) ;;

    build)
      action="build"
      shift
      benchmark.sh_build_parse_requirements "$@"
      shift $#
      ;;

    clean)
      action="clean"
      shift
      benchmark.sh_clean_parse_requirements "$@"
      shift $#
      ;;

    run)
      action="run"
      shift
      benchmark.sh_run_parse_requirements "$@"
      shift $#
      ;;

    plot)
      action="plot"
      shift
      benchmark.sh_plot_parse_requirements "$@"
      shift $#
      ;;

    # :command.command_fallback
    "")
      benchmark.sh_usage >&2
      exit 1
      ;;

    *)
      printf "invalid command: %s\n" "$action" >&2
      exit 1
      ;;

  esac

  # :command.parse_requirements_while
  while [[ $# -gt 0 ]]; do
    key="$1"
    case "$key" in

      -?*)
        printf "invalid option: %s\n" "$key" >&2
        exit 1
        ;;

      *)
        # :command.parse_requirements_case
        # :command.parse_requirements_case_simple
        printf "invalid argument: %s\n" "$key" >&2
        exit 1

        ;;

    esac
  done

}

