# src/lib/help_functions.sh
get_program_folders() {
  local -a algos langs
  local -a results=()

  # split comma lists into arrays
  IFS=',' read -ra algos <<< "${args[--algo]:-}"
  IFS=',' read -ra langs <<< "${args[--lang]:-}"

  # if empty, match all
  ((${#algos[@]})) || algos=("*")
  ((${#langs[@]})) || langs=("*")

  for a in "${algos[@]}"; do
    for l in "${langs[@]}"; do
      for d in "$algos_folder"/$a/$l; do
        [[ -d $d ]] && results+=("$d")
      done
    done
  done

  printf '%s\n' "${results[@]}"
}

exec_cmd() {
  local dir="$1"
  local cmd="$2"
  local msg="$3"

  print_info "$3: " $dir
  if ! eval $cmd; then
    print_warn "$3 failed for $dir"
  fi
}

# src/lib/out_message.sh
print_ok() {
  echo "$(green [+])" "$@"
}

print_info() {
  echo "$(blue [*])" "$@"
}

print_warn() {
  echo "$(yellow [-])" "$@"
}

print_err() {
  echo "$(red [!])" "$@"
}

