# :command.command_functions
# :command.function
benchmark.sh_build_command() {

  # src/commands/build.sh
  exec_build() {
    programs=$(get_program_folders)

    for dir in $programs; do
      case $(basename $dir) in

        java)
          exec_cmd $dir "mvn package -q -f $dir" "Building"
        ;;
        go)
          exec_cmd $dir "go build -C $dir" "Building"
        ;;
        python)
        ;;
        c | cpp)
          exec_cmd $dir "make -s -C $dir" "Building"
        ;;
      esac
    done;

    print_ok "Done"
  }

  exec_build

}

# :command.function
benchmark.sh_clean_command() {

  # src/commands/clean.sh
  remove_files() {
    local -a data

    print_info "Removing created files..."

    mapfile -t data < <(find $algos_folder -name "data.csv")
    for f in ${data[@]}; do
      rm -f $f
    done;

    rm -f $outfile
    print_ok "Done"
  }

  exec_clean() {
    programs=$(get_program_folders)

    for dir in $programs; do
      case $(basename $dir) in

        java)
          exec_cmd $dir "mvn clean -q -f $dir" "Cleaning"
        ;;
        go)
          exec_cmd $dir "go clean -C $dir" "Cleaning"
        ;;
        python)
        ;;
        c | cpp)
          exec_cmd $dir "make clean -s -C $dir" "Cleaning"
        ;;
      esac
    done;

    print_ok "Done"
  }

  exec_clean
  remove_files

}

# :command.function
benchmark.sh_run_command() {

  # src/commands/run.sh
  run() {
    local dir="$1"
    shift
    local run_args="$@"
    [[ -n "${args[--out]}" ]] && run_args+=" -o $dir/${args[--out]}"

    print_info "Running: " $dir

    if ! eval $run_args; then
      print_warn "Running failed for $dir"
    fi
  }

  exec_run() {
    programs=$(get_program_folders)
    local -a tmp=(
      "-l ${args[--lower]}"
      "-u ${args[--upper]}"
      "-s ${args[--step]}"
      "-i ${args[--iter]}"
    )

    for dir in $programs; do
      case $(basename $dir) in

        java)
          run $dir "java -jar $dir/target/sort.jar ${tmp[@]}"
        ;;
        go)
          run $dir "./$dir/sort ${tmp[@]}"
        ;;
        python)
          run $dir "python3 $dir/sort.py ${tmp[@]}"
        ;;
        c | cpp)
          run $dir "./$dir/sort ${tmp[@]}"
        ;;
      esac
    done;

    print_ok "Done"
  }

  exec_run

}

# :command.function
benchmark.sh_plot_command() {

  # src/commands/plot.sh
  names=()
  files=()

  programs=$(get_program_folders)
  for dir in $programs; do
    files+=$(find $dir -name "*.csv")
    files+=" "

    # make filepath into proper name
    # strip algo folder, remove slashes and add to names
    names+=$(echo ${dir#"$algos_folder/"} | sed 's/\//-/')
    names+=" "
  done;

  files_str="${files[*]}"
  names_str="${names[*]}"
  axis=${args[--logscale]}

  gnuplot -e "axis='$axis'; files='$files_str'; names='$names_str'; out='$outfile'" $plot_script

}

