# :command.initialize
initialize() {
  declare -g version="0.2.0"
  set -e

  # :command.variables
  # :variable.definition
  declare -g plot_folder="plotting"

  # :variable.definition
  declare -g algos_folder="algorithms"

  # :variable.definition
  declare -g plot_script="$plot_folder/plot.gp"

  # :variable.definition
  declare -g outfile="$plot_folder/graph.png"

}

# :command.run
run() {
  # :command.globals
  declare -g long_usage=''
  declare -g -A args=()
  declare -g -A deps=()
  declare -g -a env_var_names=()
  declare -g -a input=()

  normalize_input "$@"
  parse_requirements "${input[@]}"

  case "$action" in
    "build") benchmark.sh_build_command ;;
    "clean") benchmark.sh_clean_command ;;
    "run") benchmark.sh_run_command ;;
    "plot") benchmark.sh_plot_command ;;
    "all") benchmark.sh_all_command ;;
    "completions") benchmark.sh_completions_command ;;
  esac
}

if [[ "${BASH_SOURCE[0]}" == "${0}" ]]; then
  # :command.start
  command_line_args=("$@")
  initialize
  run "${command_line_args[@]}"
fi
